mapper.update:
UPDATE BM_ADMINMENU SET
	AM_NAME=#{AM_NAME},
	AM_URL=#{AM_URL},
	AM_ORDER=#{AM_ORDER},
	AM_USEYN=#{AM_USEYN}
WHERE AM_IDX=#{AM_IDX}

mapper.select:
SELECT 
	AA_ASIGN,AA_ENTR,AA_EDIT,AA_DEL,AA_EXCL
FROM BM_ADMINGRPAUTH
WHERE AM_IDX=#{AM_IDX}
AND AG_IDX=#{AG_IDX}

mapper.update:
UPDATE BM_BOOK_CATEGORY SET
	AM_NAME=#{AM_NAME},
	AM_URL=#{AM_URL},
	AM_ORDER=#{AM_ORDER},
	AM_USEYN=#{AM_USEYN}
WHERE AM_IDX=#{AM_IDX}
